import styled from "styled-components";
import { useState } from "react";

const RegisterContainer = styled.div`
  justify-content: center;
  background-color: white;
  flex-direction: column;
  font-size: 2.5rem;
`;
const RegisterGNB = styled.h1``;
const RegisterForm = styled.form`
  overflow-y: scroll;
  padding-bottom: 10rem;
`;
const RegisterLabel = styled.label`
  &:nth-child(12) {
    flex-direction: row;
    align-items: center;
    input {
      width: 5vw;
      height: 5vw;
    }
  }
`;
const RegisterInput = styled.input``;
const RegisterBTN = styled.button`
  display: flex;
  height: 7rem;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  border-radius: 5rem;
  right: 5vw !important;
  bottom: 5rem;
`;

function Register() {
  const [id, setId] = useState("");
  const [password, setPassword] = useState("");
  const [confirm, setConfirm] = useState("");

  const [idError, setIdError] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [confirmError, setConfirmError] = useState("");

  const [isIdCheck, setIsIdCheck] = useState(false); // 중복 검사를 했는지 안했는지
  const [isIdAvailable, setIsIdAvailable] = useState(false); // 아이디 사용 가능한지 아닌지

  const idCheckHandler = async (id) => {
    const idRegex = /^[a-z\d]{5,10}$/;
    if (id === "") {
      setIdError("아이디를 입력해주세요.");
      setIsIdAvailable(false);
      return false;
    } else if (!idRegex.test(id)) {
      setIdError("아이디는 5~10자의 영소문자, 숫자만 입력 가능합니다.");
      setIsIdAvailable(false);
      return false;
    }
    try {
      const responseData = await idDuplicateCheck(id);
      if (responseData) {
        setIdError("사용 가능한 아이디입니다.");
        setIsIdCheck(true);
        setIsIdAvailable(true);
        return true;
      } else {
        setIdError("이미 사용중인 아이디입니다.");
        setIsIdAvailable(false);
        return false;
      }
    } catch (error) {
      alert("서버 오류입니다. 관리자에게 문의하세요.");
      console.error(error);
      return false;
    }
  };

  const onChangeIdHandler = (e) => {
    const idValue = e.target.value;
    setId(idValue);
    idCheckHandler(idValue);
  };

  const passwordCheckHandler = (password, confirm) => {
    const passwordRegex = /^[a-z\d!@*&-_]{8,16}$/;
    if (password === "") {
      setPasswordError("비밀번호를 입력해주세요.");
      return false;
    } else if (!passwordRegex.test(password)) {
      setPasswordError(
        "비밀번호는 8~16자의 영소문자, 숫자, !@*&-_만 입력 가능합니다."
      );
      return false;
    } else if (confirm !== password) {
      setPasswordError("");
      setConfirmError("비밀번호가 일치하지 않습니다.");
      return false;
    } else {
      setPasswordError("");
      setConfirmError("");
      return true;
    }
  };

  const onChangePasswordHandler = (e) => {
    const { name, value } = e.target;
    if (name === "password") {
      setPassword(value);
      passwordCheckHandler(value, confirm);
    } else {
      setConfirm(value);
      passwordCheckHandler(password, value);
    }
  };

  const signupHandler = async (e) => {
    e.preventDefault();

    const idCheckresult = await idCheckHandler(id);
    if (idCheckresult) setIdError("");
    else return;
    if (!isIdCheck || !isIdAvailable) {
      alert("아이디 중복 검사를 해주세요.");
      return;
    }

    const passwordCheckResult = passwordCheckHandler(password, confirm);
    if (passwordCheckResult) {
      setPasswordError("");
      setConfirmError("");
    } else return;

    try {
      const responseData = await signup(id, password, confirm);
      if (responseData) {
        localStorage.setItem("loginId", id);
        setOpenModal(true);
      } else {
        alert("회원가입에 실패하였습니다. 다시 시도해주세요.");
      }
    } catch (error) {
      alert("회원가입에 실패하였습니다. 다시 시도해주세요.");
      console.error(error);
    }
  };

  return (
    <>
      <RegisterContainer className="container">
        <RegisterForm className="form">
          <br />
          <RegisterLabel className="label">
            이름
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <br />
          <RegisterLabel className="label">
            이메일
            <RegisterInput className="input" type="email" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            비밀번호
            <RegisterInput className="input" type="password" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            비밀번호 확인
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            전화번호
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <br />
          <RegisterLabel className="label">
            성인입니다.
            <br />
            <RegisterInput className="input checkbox" type="checkbox" />
          </RegisterLabel>
          <br />
          <RegisterBTN className="submit_btn" type="submit">
            회원가입
          </RegisterBTN>
        </RegisterForm>
      </RegisterContainer>
    </>
  );
}

export default Register;
