import styled from "styled-components";

const EventsHeader = styled.h1`
  padding-left: 2rem;
  font-size: 5rem;
  font-weight: 600;
  background-color: var(--orange);
  color: var(--dark-brown);
  width: 100vw;
  height: 10rem;
  padding: 2rem 0;
  position: sticky;
  box-sizing: border-box;
  top: 0rem;
  margin: 1px;
`;

const EventContainer = styled.li`
  display: flex;
  width: 100%;
  height: var(--mobil-screen);
  flex-direction: column;
  border: 2px solid var(--border);
  list-style: none;
`;

const EventPosterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 900px;
  height: 1600px;
`;

const EventPoster = styled.div`
  align-items: center;
  width: 80%;
  height: 100%;
  background-color: red;
  object-fit: contain;
  border: 2px solid var(--border);
`;

const EventTitle = styled.h2`
  font-size: 5rem;
  font-weight: 600;
  display: flex;
  justify-content: flex-start;
`;
const EventInfoContainer = styled.div`
  width: 90%;
  height: 30%;
`;
const EventInfo = styled.p`
  width: 100%;
  font-size: 3rem;
  font-weight: 300;
`;

function Event(
  {
    /* title, poster, info */
  }
) {
  const Events = styled.ul`
    display: flex;
    flex-direction: column;
    justify-content: flex-start;
    width: 100vw;
    height: calc(300vh - 36rem);
    gap: 1rem;
    background-color: white;
    padding-top: 24rem;
  `;
  return (
    <div id="GlobalContainer">
      <EventsHeader>이벤트 목록</EventsHeader>
      <Events>
        <EventContainer>
          <EventTitle>Monn&Tree x Magicclub Close-up Workshop</EventTitle>
          <EventPosterContainer>
            <EventPoster></EventPoster>
          </EventPosterContainer>
          <EventInfoContainer>
            <EventInfo>
              장소: 블라인드 아트홀 <br />
              일시: 2024. 04. 06. 18:00 ~ 20:00
              <br /> 인원: ~ 40명
            </EventInfo>
          </EventInfoContainer>
        </EventContainer>
        <EventContainer>
          <EventTitle>Party</EventTitle>
          <EventPosterContainer>
            <EventPoster></EventPoster>
          </EventPosterContainer>
          <EventInfoContainer>
            <EventInfo>
              장소: 몰?루? <br />
              일시: 2024. 03. 24. 14:00 ~ 18:00 <br />
              인원: ~ 40명
            </EventInfo>
          </EventInfoContainer>
        </EventContainer>
        <EventContainer>
          <EventTitle>Zero Sky</EventTitle>
          <EventPosterContainer>
            <EventPoster></EventPoster>
          </EventPosterContainer>
          <EventInfoContainer>
            <EventInfo>
              장소: 미정이 <br />
              일시: 2024. 05. 26. 18:00 ~ 20:00 <br />
              인원: ~ 80명
            </EventInfo>
          </EventInfoContainer>
        </EventContainer>
        {/*Events.map((EventAPI, index) => (
          <EventContainer className="EventAPI__name" key={index}>
            <EventPoster>{poster}</EventPoster>
            {title}
            {info}
          </EventContainer>
        ))*/}
      </Events>
    </div>
  );
}
{
  /*
EventAPI.propTypes = {
    title: PropTypes.string.isRequired,
    poster: PropTypes.img.isRequired,
    info: PropTypes.string.isRequired,
  };
*/
}
export default Event;
