import { useState } from "react";
import styled from "styled-components";

const MyPageContainer = styled.div``;

const StaticInfos = styled.div`
  width: 100%;
  height: 25vh;
  margin: 0rem;
  display: flex;
  flex-direction: column;
  padding: 4rem 5vw;
  font-size: 3rem;
  line-height: 5rem;
`;

const StaticInfo = styled.span``;

const UpdateForm = styled.form`
  padding-bottom: 15rem !important;
`;

const UpdateHeader = styled.h1`
  font-size: 3rem;
`;

const UpdateLabel = styled.label`
  font-size: 2.5rem;
`;

const UpdateInput = styled.input``;

const UpdateBTN = styled.button`
  display: flex;
  height: 7rem;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  border-radius: 5rem;
  right: 5vw !important;
  bottom: 5%;
`;

function MyPage() {
  const [email, setEmail] = useState("");
  const onChange = (event) => {
    console.log(event);
  };
  const EmailCheck = (event: any) => {
    const newEmail = event.target.email.value;
    event.preventDefault();
    console.log(newEmail);
  };
  return (
    <>
      <MyPageContainer className="container">
        <StaticInfos>
          <StaticInfo>이름: 홍길동</StaticInfo>
          <StaticInfo>회원 등급: 정회원</StaticInfo>
        </StaticInfos>

        <UpdateForm className="form">
          <UpdateHeader>개인정보 변경</UpdateHeader>
          <br />
          <UpdateLabel className="label">
            이메일
            <br />
            <UpdateInput
              className="input"
              type="email"
              value="email"
              onChange={onChange}
              required
            />
          </UpdateLabel>
          <hr />
          <UpdateLabel className="label">
            이전 비밀번호
            <br />
            <UpdateInput className="input" type="text" required />
          </UpdateLabel>
          <hr />
          <UpdateLabel className="label">
            새 비밀번호
            <br />
            <UpdateInput className="input" type="" required />
          </UpdateLabel>
          <hr />
          <UpdateLabel className="label">
            새로운 비밀번호 확인
            <br />
            <UpdateInput className="input" type="password" required />
          </UpdateLabel>
          <hr />
          <UpdateLabel className="label">
            새 전화번호
            <br />
            <UpdateInput className="input" type="text" required />
          </UpdateLabel>

          <UpdateBTN className="submit_btn" type="submit" onSubmit={EmailCheck}>
            개인정보 변경
          </UpdateBTN>
        </UpdateForm>
      </MyPageContainer>
    </>
  );
}

export default MyPage;
