import styled from "styled-components";
import PropTypes from "prop-types";

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 5rem;
  width: 100%;
  height: calc(100vh - 5rem);
  flex-direction: column;
`;

const Notices = styled.div`
  width: 100%;
  height: 100%;
  display: grid;
  grid-template-rows: 1.7rem repeat(auto-fit, 1.5rem);
  background-color: white;
`;

const NoticeHeader = styled.div`
  display: grid;
  grid-template-columns: 1fr 7fr 2fr;
  font-size: 1.2rem;
  font-weight: 600;
  border: 1px solid var(--border);
  box-sizing: border-box;
`;

const NoticeCollumn = styled(NoticeHeader)`
  font-size: 1rem;
`;

const NoticeText = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
`;

function Notice(
  {
    /* title, date */
  }
) {
  return (
    <Container>
      <Notices>
        <NoticeHeader>
          <NoticeText>순번</NoticeText>
          <NoticeText>제목</NoticeText>
          <NoticeText>날짜</NoticeText>
        </NoticeHeader>
        <NoticeCollumn>
          <NoticeText>1</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        <NoticeCollumn>
          <NoticeText>2</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        <NoticeCollumn>
          <NoticeText>3</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        {/*genres.map((NoticeAPI, index) => (
          <NoticeCololumn className="NoticeAPI__name" key={index}>
            <span>{index}</span>
            <span>{title}</span>
            <span>{date}</span>
          </NoticeCololumn>
        ))*/}
      </Notices>
    </Container>
  );
}
