import React, { useState } from "react";
import styled from "styled-components";

interface MainProps {
  images: string[];
}

const MainContainer = styled.div`
  position: relative;
  width: 100%;
  overflow: hidden;
`;

const SlideImage = styled.img`
  width: 100%;
  height: auto;
`;

const SlideButton = styled.button`
  position: absolute;
  top: 50%;
  transform: translateY(-50%);
  background-color: rgba(255, 255, 255, 0.5);
  border: none;
  color: black;
  padding: 10px 20px;
  cursor: pointer;
`;

const PrevButton = styled(SlideButton)`
  left: 0;
`;

const NextButton = styled(SlideButton)`
  right: 0;
`;

const Main: React.FC<MainProps> = ({ images }) => {
  const [currentIndex, setCurrentIndex] = useState(0);

  const nextSlide = () => {
    const nextIndex = (currentIndex + 1) % images.length;
    setCurrentIndex(nextIndex);
  };

  const prevSlide = () => {
    const prevIndex = (currentIndex - 1 + images.length) % images.length;
    setCurrentIndex(prevIndex);
  };

  return (
    <div id="GlobalContainer">
      <PrevButton onClick={prevSlide}>이전</PrevButton>
      <SlideImage src={images[currentIndex]} alt="Slide" />
      <NextButton onClick={nextSlide}>다음</NextButton>
    </div>
  );
};

export default Main;
