import React, { useState, useEffect } from "react";
import styled from "styled-components";

const MainIMGs: React.FC = () => {
  const [images, setImages] = useState<string[]>([]);
  const [currentImageIndex, setCurrentImageIndex] = useState<number>(0);

  const Album = styled.li`
    background-color: var(--cream);
  `;

  const Photo = styled.img`
    width: 300px;
    height: 300px;
    background-color: var(--brown);
  `;

  useEffect(() => {
    // 10장의 이미지 경로를 생성
    const imagePaths = Array.from(
      { length: 10 },
      (_, index) => `image${index + 1}.jpg`
    );
    setImages(imagePaths);

    // 3초마다 이미지 변경
    const interval = setInterval(() => {
      setCurrentImageIndex((prevIndex) => {
        let randomIndex = Math.floor(Math.random() * imagePaths.length);
        while (randomIndex === prevIndex) {
          randomIndex = Math.floor(Math.random() * imagePaths.length);
        }
        return randomIndex;
      });
    }, 3000);

    return () => clearInterval(interval);
  }, []);

  return (
    <Album>
      {images.map((image, index) => (
        <Photo
          key={index}
          style={{ display: index === currentImageIndex ? "block" : "none" }}
        >
          <img src={`/past/${image}`} alt={`Image ${index + 1}`} />
        </Photo>
      ))}
    </Album>
  );
};

export default MainIMGs;
