import styled from "styled-components";
import PropTypes from "prop-types";
import { useState } from "react";

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 5rem;
  width: 100%;
  height: calc(100vh - 5rem);
  flex-direction: column;
`;

const WritingForm = styled.form`
  width: 90%;
  height: 80%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  overflow: hidden;
`;
const TitleInput = styled.input`
  width: 90%;
  height: 2rem;
  font-size: 1.7rem;
  border: 1px solid var(--border);
  box-sizing: border-box;
`;
const ContentArea = styled.textarea`
  width: 90%;
  height: 80%;
  border: 1px solid var(--border);
  overflow-y: scroll;
`;
const UploadBtn = styled.button`
  width: 3rem;
  height: 2rem;
`;

function NoticeWrite() {
  const [title, setTitle] = useState("");
  const titleChange = (event: React.FormEvent<HTMLInputElement>) => {
    const {
      currentTarget: { title },
    } = event;
    setTitle(title);
  };
  const [value, setValue] = useState("");
  const valueChange = (event: React.FormEvent<HTMLTextAreaElement>) => {
    const {
      currentTarget: { value },
    } = event;
    setValue(value);
  };
  const onSubmit = (event: React.FormEvent<HTMLFormElement>) => {
    event.preventDefault();
    console.log({ title });
    console.log({ value });
  };

  return (
    <Container>
      <WritingForm onSubmit={onSubmit}>
        <TitleInput onChange={titleChange} />
        <ContentArea onChange={valueChange} />
        <UploadBtn>공지 하기</UploadBtn>
      </WritingForm>
    </Container>
  );
}

export default NoticeWrite;
