import styled from "styled-components";

const EventsHeader = styled.h1`
  display: flex;
  justify-content: center;
  align-items: flex-end;
  font-size: 7rem;
  font-weight: 600;
  background-color: white;
  width: 100vw;
  padding: 1rem 0;
  position: sticky;
  top: 0;
`;

const EventContainer = styled.li`
  display: flex;
  width: 100%;
  height: var(--mobil-screen);
  flex-direction: column;
  border: 2px solid var(--border);
  list-style: none;
`;

const EventPosterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const EventPoster = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 80%;
  height: 100%;
  background-color: red;
  object-fit: contain;
  border: 2px solid var(--border);
`;

const EventTitle = styled.h2`
  font-size: 5rem;
  font-weight: 600;
  display: flex;
  justify-content: flex-start;
`;
const EventInfoContainer = styled.div`
  width: 90%;
  height: 30%;
`;
const EventInfo = styled.p`
  width: 100%;
  font-size: 3rem;
  font-weight: 300;
`;

function Event(
  {
    /* title, poster, info */
  }
) {
  const Events = styled.ul`
    display: flex;
    flex-direction: column;
    width: 100vw;
    height: calc(300vh - 36rem);
    gap: 1rem;
    background-color: white;
  `;
  return (
    <div id="GlobalContainer">
      <EventsHeader>이벤트 목록</EventsHeader>
      <Events>
        <EventContainer>
          <EventTitle>Monn&Tree x Magicclub Close-up Workshop</EventTitle>
          <EventPoster></EventPoster>
          <EventInfoContainer>
            <EventInfo>
              장소: 블라인드 아트홀 <br />
              일시: 2024. 04. 06. 18:00 ~ 20:00 인원: ~ 40명
            </EventInfo>
          </EventInfoContainer>
        </EventContainer>
        <EventContainer>Party</EventContainer>
        <EventContainer>5월 공연</EventContainer>
        {/*Events.map((EventAPI, index) => (
          <EventContainer className="EventAPI__name" key={index}>
            <EventPoster>{poster}</EventPoster>
            {title}
            {info}
          </EventContainer>
        ))*/}
      </Events>
    </div>
  );
}
{
  /*
EventAPI.propTypes = {
    title: PropTypes.string.isRequired,
    poster: PropTypes.img.isRequired,
    info: PropTypes.string.isRequired,
  };
*/
}
export default Event;
