import styled from "styled-components";

const EventsContainer = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  justify-content: flex-start;
  margin-top: 44rem;
`;

const EventsHeader = styled.h1`
  display: flex;
  align-items: center;
  font-size: 5rem;
  font-weight: 600;
  background-color: purple;
  width: 100vw;
  height: 10rem;
  padding: 2rem;
  position: fixed;
  box-sizing: border-box;
  top: 12.5rem;
  margin: 1px;
`;

const EventContainer = styled.li`
  display: flex;
  flex-direction: column;
  width: 100%;
  height: calc(var(--mobile-screen) - 10rem);
  align-items: center;
  border: 2px solid var(--border);
  list-style: none;
  box-sizing: border-box;
`;

const EventPosterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const EventPoster = styled.div`
  align-items: center;
  width: 900px;
  height: 1600px;
  background-color: red;
  object-fit: contain;
  border: 2px solid var(--border);
`;

const EventTitle = styled.h2`
  font-size: 5rem;
  font-weight: 600;
  display: flex;
  justify-content: flex-start;
  padding: 2rem;
`;
const EventInfoContainer = styled.div`
  width: 90%;
  height: 30%;
`;
const EventInfo = styled.p`
  width: 100%;
  font-size: 3rem;
  font-weight: 300;
`;

function Event(
  {
    /* title, poster, info */
  }
) {
  const Events = styled.ul`
    width: 100vw;
    height: calc(300vh - 102rem);
    background-color: white;
  `;
  return (
    <div id="GlobalContainer">
      <EventsContainer>
        <EventsHeader>이벤트 목록</EventsHeader>
        <Events>
          <EventContainer>
            <EventTitle>Monn&Tree x Magicclub Close-up Workshop</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 블라인드 아트홀 <br />
                일시: 2024. 04. 06. 18:00 ~ 20:00
                <br /> 인원: ~ 40명
              </EventInfo>
            </EventInfoContainer>
          </EventContainer>
          <EventContainer>
            <EventTitle>Party</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 몰?루? <br />
                일시: 2024. 03. 24. 14:00 ~ 18:00 <br />
                인원: ~ 40명
              </EventInfo>
            </EventInfoContainer>
          </EventContainer>
          <EventContainer>
            <EventTitle>Zero Sky</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 미정이 <br />
                일시: 2024. 05. 26. 18:00 ~ 20:00 <br />
                인원: ~ 80명
              </EventInfo>
            </EventInfoContainer>
          </EventContainer>
          {/*Events.map((EventAPI, index) => (
          <EventContainer className="EventAPI__name" key={index}>
            <EventPoster>{poster}</EventPoster>
            {title}
            {info}
          </EventContainer>
        ))*/}
        </Events>
      </EventsContainer>
    </div>
  );
}
{
  /*
EventAPI.propTypes = {
    title: PropTypes.string.isRequired,
    poster: PropTypes.img.isRequired,
    info: PropTypes.string.isRequired,
  };
*/
}
export default Event;
