import styled from "styled-components";

const MyPageContainer = styled.div`
  margin: 0 !important;
  justify-content: center;
  background-color: white;
  flex-direction: column;
  height: calc() (var(--mobile-screen) + 40vh);
  font-size: 4rem;
`;

const MyPageGNB = styled.h1``;

const StaticInfos = styled.div`
  width: 100%;
  background-color: red;
  margin-top: 20.5rem;
  display: flex;
  flex-direction: column;
  padding: 2rem;
`;

const StaticInfo = styled.span``;

const UpdateForm = styled.form`
  display: flex;
  width: 100%;
  height: var(--mobile-screen);
  flex-direction: column;
  justify-content: space-around;
  align-items: flex-start;
  background-color: white;
  padding: 2rem;
  padding-bottom: 4rem;
  border: 5px solid green;
  position: relative;
`;

const UpdateLabel = styled.label`
  display: flex;
  flex-direction: column;
  position: relative;
  width: 100%;
  height: 15rem;
  gap: 2rem;
`;

const UpdateInput = styled.input`
  display: flex;
  width: 75%;
  height: 5rem;
  justify-content: flex-start;
  align-items: center;
  margin-bottom: 10rem;
  background-color: transparent;
  border: 1px solid black;
  position: absolute;
  top: 50%;
  left: 50%;
  transform: translate(-50%, -50%);
`;

const UpdateBTN = styled.button`
  position: absolute;
  right: 2rem;
  bottom: 2rem;
  width: 35%;
  height: 6rem;
  font-size: 2.5rem;
  background-color: transparent;
  &:hover {
    transform: scale(1.2);
  }
`;

function MyPage() {
  return (
    <div id="GlobalContainer">
      <MyPageContainer className="container">
        <MyPageGNB className="gnb">마이 페이지</MyPageGNB>
        <StaticInfos>
          <StaticInfo>이름: 홍길동</StaticInfo>
          <br />
          <StaticInfo>회원 등급: 정회원</StaticInfo>
        </StaticInfos>
        <hr />
        <UpdateForm>
          <UpdateLabel>
            이메일:
            <br />
            <UpdateInput type="text" />
          </UpdateLabel>
          <hr />
          <UpdateLabel>
            이전 비밀번호:
            <br />
            <UpdateInput type="text" />
          </UpdateLabel>
          <hr />
          <UpdateLabel>
            새 비밀번호:
            <br />
            <UpdateInput type="text" />
          </UpdateLabel>
          <hr />
          <UpdateLabel>
            새로운 비밀번호 확인:
            <br />
            <UpdateInput type="text" />
          </UpdateLabel>

          <UpdateBTN type="submit">개인정보 변경</UpdateBTN>
        </UpdateForm>
      </MyPageContainer>
    </div>
  );
}

export default MyPage;
