import styled from "styled-components";

const EventsContainer = styled.div``;

const EventsHeader = styled.h1``;

const EventList = styled.li``;

const EventPosterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const EventPoster = styled.div`
  align-items: center;
  width: 540px;
  height: 960px;
  background-color: red;
  object-fit: contain;
  border: 2px solid var(--border);
`;

const EventTitle = styled.h2`
  font-size: 5rem;
  font-weight: 600;
  display: flex;
  justify-content: flex-start;
  padding: 2rem;
`;
const EventInfoContainer = styled.div`
  width: 90%;
  height: 30%;
  padding: 5rem 3rem;
`;
const EventInfo = styled.p`
  width: 100%;
  font-size: 4rem;
  font-weight: 300;
`;

function Event(
  {
    /* title, poster, info */
  }
) {
  const Events = styled.ul`
    width: 100vw;
    height: calc(300vh - 102rem);
    background-color: white;
    gap: 1px;
    box-sizing: border-box;
  `;
  return (
    <div id="GlobalContainer">
      <EventsContainer className="container">
        <EventsHeader className="gnb">이벤트 목록</EventsHeader>
        <Events>
          <EventList className="list">
            <EventTitle>Monn&Tree x Magicclub Close-up Workshop</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 블라인드 아트홀 <br />
                일시: 2024. 04. 06. 18:00 ~ 20:00 <br />
                인원: ~ 40명 <br />
                참가비: 무료
              </EventInfo>
            </EventInfoContainer>
          </EventList>
          <EventList className="list">
            <EventTitle>Party</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 몰?루? <br />
                일시: 2024. 03. 24. 14:00 ~ 18:00 <br />
                인원: ~ 40명
                <br />
                참가비: 15,000원
              </EventInfo>
            </EventInfoContainer>
          </EventList>
          <EventList className="list">
            <EventTitle>Zero Sky</EventTitle>
            <EventPosterContainer>
              <EventPoster></EventPoster>
            </EventPosterContainer>
            <EventInfoContainer>
              <EventInfo>
                장소: 미정 <br />
                일시: 2024. 05. 26. 18:00 ~ 20:00 <br />
                인원: ~ 80명 <br />
                참가비: 28,000원
              </EventInfo>
            </EventInfoContainer>
          </EventList>
          {/*Events.map((EventAPI, index) => (
          <EventList className="list" className="EventAPI__name" key={index}>
            <EventPoster>{poster}</EventPoster>
            {title}
            {info}
          </EventList>
        ))*/}
        </Events>
      </EventsContainer>
    </div>
  );
}
{
  /*
EventAPI.propTypes = {
    title: PropTypes.string.isRequired,
    poster: PropTypes.img.isRequired,
    info: PropTypes.string.isRequired,
  };
*/
}
export default Event;
