import styled from "styled-components";
import { useState } from "react";

const RegisterContainer = styled.div`
  justify-content: center;
  background-color: white;
  flex-direction: column;
  font-size: 2.5rem;
`;
const RegisterGNB = styled.h1``;
const RegisterForm = styled.form`
  overflow-y: scroll;
  padding-bottom: 10rem;
`;
const RegisterLabel = styled.label`
  &:nth-child(12) {
    flex-direction: row;
    align-items: center;
    input {
      width: 5vw;
      height: 5vw;
    }
  }
`;
const RegisterInput = styled.input``;
const RegisterBTN = styled.button`
  display: flex;
  height: 7rem;
  align-items: center;
  justify-content: center;
  font-weight: 600;
  border-radius: 5rem;
  right: 5vw !important;
  bottom: 5rem;
`;

function Register() {
  const [Email, setEmail] = useState("");
  const [pw, setPW] = useState("");
  const [confirm, setConfirm] = useState("");

  const [EmailError, setEmailError] = useState("");
  const [pwError, setPWError] = useState("");
  const [confirmError, setConfirmError] = useState("");

  const [isEmailCheck, setIsEmailCheck] = useState(false); // 중복 검사를 했는지 안했는지
  const [isPWAvailable, setIsPWAvailable] = useState(false); // 이메일 사용 가능한지 아닌지

  return (
    <>
      <RegisterContainer className="container">
        <RegisterForm className="form">
          <br />
          <RegisterLabel className="label">
            이름
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <br />
          <RegisterLabel className="label">
            이메일
            <RegisterInput className="input" type="email" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            비밀번호
            <RegisterInput className="input" type="password" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            비밀번호 확인
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <hr />
          <RegisterLabel className="label">
            전화번호
            <RegisterInput className="input" type="text" />
          </RegisterLabel>
          <br />
          <RegisterLabel className="label">
            성인입니다.
            <br />
            <RegisterInput className="input checkbox" type="checkbox" />
          </RegisterLabel>
          <br />
          <RegisterBTN className="submit_btn" type="submit">
            회원가입
          </RegisterBTN>
        </RegisterForm>
      </RegisterContainer>
    </>
  );
}

export default Register;
