import styled from "styled-components";

const Notices = styled.div`
  width: 100%;
  height: 100%;
  display: grid;
  grid-template-rows: 2rem repeat(auto-fit, 1.5rem);
  background-color: white;
`;

const NoticeHeader = styled.div`
  display: grid;
  grid-template-columns: 1fr 7fr 2fr;
  font-size: 1.2rem;
  font-weight: 600;
  border: 1px solid var(--border);
  box-sizing: border-box;
`;

const NoticeCollumn = styled(NoticeHeader)`
  font-size: 1rem;
  font-weight: 300;
`;

const NoticeText = styled.span`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
`;
interface NoticeCollumnProps {
  index: number;
  title: string;
  date: string;
}
{
  /*
function NoticeCollumn({ index, title, date }: NoticeCollumnProps) {
  return (
    <div>
      <NoticeText>{index}</NoticeText>
      <NoticeText>{title}</NoticeText>
      <NoticeText>{date}</NoticeText>
    </div>
  );
}
*/
}
function Notice() {
  return (
    <div id="GlobalContainer">
      <Notices>
        <NoticeHeader>
          <NoticeText>순번</NoticeText>
          <NoticeText>제목</NoticeText>
          <NoticeText>날짜</NoticeText>
        </NoticeHeader>
        <NoticeCollumn>
          <NoticeText>1</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        <NoticeCollumn>
          <NoticeText>2</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        <NoticeCollumn>
          <NoticeText>3</NoticeText>
          <NoticeText>공지 제목</NoticeText>
          <NoticeText>2024.03.04</NoticeText>
        </NoticeCollumn>
        {/*genres.map((NoticeAPI, index) => (
          <NoticeCololumn className="NoticeAPI__name" key={index}>
            <span>{index}</span>
            <span>{title}</span>
            <span>{date}</span>
          </NoticeCololumn>
        ))*/}
      </Notices>
    </div>
  );
}

export default Notice;
