import styled from "styled-components";
import PropTypes from "prop-types";

const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 5rem;
  width: 100%;
  height: calc(100vh - 5rem);
  flex-direction: column;
`;

const NavTitle = styled.div`
  width: 100%;
  height: 3rem;
  border: 3px solid var(--dark-brown);
  background-color: var(--red-brown);
  display: flex;
  justify-content: flex-start;
  align-items: center;
  padding-left: 1rem;
  box-sizing: border-box;
`;
const NavTitleText = styled.span`
  font-size: 1rem;
  font-weight: 600;
  color: var(--cream);
`;

const WritingForm = styled.form`
  width: 90%;
  height: 80%;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
  justify-content: center;
  overflow: hidden;
`;
const TitleInput = styled.input`
  width: 90%;
  height: 2rem;
  font-size: 1.7rem;
  border: 1px solid var(--border);
  box-sizing: border-box;
`;
const ContentArea = styled.textarea`
  width: 90%;
  height: 80%;
  border: 1px solid var(--border);
  overflow-y: scroll;
`;
function NoticeWrite() {
  return (
    <Container>
      <NavTitle>
        <NavTitleText>공지 작성</NavTitleText>
      </NavTitle>
      <WritingForm>
        <TitleInput />
        <ContentArea />
      </WritingForm>
    </Container>
  );
}

export default NoticeWrite;
