import React, { useState, useEffect } from "react";
import styled from "styled-components";

const AlbumItem = styled.li`
  position: relative;
  list-style: none;
  height: 100vh;
  overflow: hidden;
`;

const Photo = styled.img`
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  transition: top 0.5s ease-in-out;
`;

const Gallery: React.FC = () => {
  const [images, setImages] = useState<string[]>([]);
  const [currentIndex, setCurrentIndex] = useState<number>(0);

  useEffect(() => {
    // 10장의 이미지 경로를 생성
    const imagePaths = Array.from(
      { length: 10 },
      (_, index) => `image${index + 1}.jpg`
    );
    setImages(imagePaths);

    // 스크롤 이벤트 핸들러 등록
    const handleScroll = () => {
      const { scrollTop, clientHeight, scrollHeight } =
        document.documentElement;
      if (scrollTop + clientHeight >= scrollHeight * 0.75) {
        // 스크롤이 하단에 도달하면 다음 이미지 보여주기
        setCurrentIndex((prevIndex) => (prevIndex + 1) % imagePaths.length);
      } else if (scrollTop <= scrollHeight * 0.25) {
        // 스크롤이 상단에 도달하면 이전 이미지 보여주기
        setCurrentIndex(
          (prevIndex) => (prevIndex - 1 + imagePaths.length) % imagePaths.length
        );
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <ul>
      {images.map((image, index) => (
        <AlbumItem key={index}>
          <Photo
            src={`/past/${image}`}
            alt={`Image ${index + 1}`}
            style={{ top: `${(index - currentIndex) * 100}vh` }}
          />
        </AlbumItem>
      ))}
    </ul>
  );
};

export default Gallery;
