import styled from "styled-components";
const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  padding-top: 5rem;
  background-color: aliceblue;
  width: 100%;
  height: calc(100vh - 5rem);
  flex-direction: column;
`;

const Events = styled.ul`
  width: 100vw;
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(4rem, auto));
  gap: 1rem;
`;

const EventContainer = styled.li`
  display: flex;
  flex-direction: column;
  border: 2px solid var(--border);
  list-style: none;
`;

const EventPosterContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
`;

const EventPoster = styled.img`
  width: 100%;
  height: 100%;
  object-fit: contain;
`;

function Event() {
  return (
    <Container>
      <Events>
        {genres.map((EventAPI, index) => (
          <EventContainer className="EventAPI__name" key={index}>
            {EventAPI}
          </EventContainer>
        ))}
      </Events>
    </Container>
  );
}

export default Event;
